const formatNumber=n=>n.toString().replace(/\B(?=(\d{3})+(?!\d))/g," ");const formatDate=d=>{const m=["янв","фев","мар","апр","май","июн","июл","авг","сен","окт","ноя","дек"];return`${d.getDate()} ${m[d.getMonth()]}`};const formatTime=d=>`${d.getHours().toString().padStart(2,"0")}:${d.getMinutes().toString().padStart(2,"0")}`;const getRandomInt=(min,max)=>Math.floor(Math.random()*(max-min+1))+min;const sleep=ms=>new Promise(r=>setTimeout(r,ms));const copyToClipboard=text=>{const t=document.createElement("textarea");t.value=text;document.body.appendChild(t);t.select();document.execCommand("copy");document.body.removeChild(t)};const debounce=(func,wait)=>{let timeout;return function executedFunction(...args){const later=()=>{clearTimeout(timeout);func(...args)};clearTimeout(timeout);timeout=setTimeout(later,wait)}};const throttle=(func,limit)=>{let inThrottle;return function(...args){if(!inThrottle){func.apply(this,args);inThrottle=true;setTimeout(()=>inThrottle=false,limit)}}}; 